import random

print("Guess the number")

while True:
    try:
        range1 = int(input("Please enter one of the limits: "))
        range2 = int(input("Please enter one of the limits: "))
        if range1 > range2:
            number = random.randint(range2, range1)
        elif range2 > range1:
            number = random.randint(range1, range2)
        else:
            print("The intervale is not valid")
            continue

        guess = 0
        while number != guess:
            guess = int(input("Guess the number: "))
            if number > guess:
                print("The real number is greater than the guess.")
            elif number < guess:
                print("The real number is less than the guess.")
        print("You guessed the number!")

        response = input("Would you like to continue? Press Y to continue ").capitalize()
        if response != "Y":
            break
    except ValueError:
        print("This is not a valid number.")

import random

# Brief: Simple number guessing game using a random number within user-provided bounds.
# Input: ask for two limits, validate and create a random number in the interval.
# Guess loop: repeatedly prompt for guesses, guide the user with higher/lower hints.
# Repeat: after a correct guess ask whether to play again; invalid inputs handled with try/except.

print("Guess the number")

while True:
    try:
        range1 = int(input("Please enter one of the limits: "))
        range2 = int(input("Please enter one of the limits: "))
        if range1 > range2:
            number = random.randint(range2, range1)
        elif range2 > range1:
            number = random.randint(range1, range2)
        else:
            print("The intervale is not valid")
            continue

        guess = 0
        while number != guess:
            guess = int(input("Guess the number: "))
            if number > guess:
                print("The real number is greater than the guess.")
            elif number < guess:
                print("The real number is less than the guess.")
        print("You guessed the number!")

        response = input("Would you like to continue? Press Y to continue ").capitalize()
        if response != "Y":
            break
    except ValueError:
        print("This is not a valid number.")

print("Do you want to learn the multiplication table?")

while True:
    try:
        number = int(input("Enter the number to generate its multiplication table: "))
        for i in range(11):
            result = number * i
            print(f"{number} * {i} = {result}")

        response = input("Would you like to continue? Press Y to continue ").capitalize()
        if response != "Y":
            break
    except ValueError:
        print("This is not a valid number.")

